package com.example.pokeapi.data.datasource.remote.modelsimport com.squareup.moshi.Jsonimport com.squareup.moshi.JsonClass@JsonClass(generateAdapter = true)data class PokemonResponse(    val id: Int,    val name: String,    val height: Int,    val weight: Int,    val types: List<Types>,    val sprites: Sprites) {    @JsonClass(generateAdapter = true)    data class Types(        val slot: Int,        val type: Type    ) {        @JsonClass(generateAdapter = true)        data class Type(            val name: String,            val url: String        )    }    @JsonClass(generateAdapter = true)    data class Sprites(        @field:Json(name = "front_default")        val frontDefault: String    )}